var newMsg = {payload: msg.payload};

// Power in watts
newMsg["compressorPower"] = msg.current * msg.acVoltage;
newMsg["compressorPowerRounded"] = Math.round(newMsg["compressorPower"]);

// Load in watts
if (msg.mode == "cool" && msg.inletWaterTemp >= msg.outletWaterTemp)
    newMsg["primaryLoad"] = msg.waterFlowUnrounded * (msg.inletWaterTemp - msg.outletWaterTemp) * 500.4 / 3.412;
else if ((msg.mode == "heat" || msg.mode == "defrost") && msg.inletWaterTemp <= msg.outletWaterTemp)
    newMsg["primaryLoad"] = msg.waterFlowUnrounded * (msg.outletWaterTemp - msg.inletWaterTemp) * 500.4 / 3.412;
else
    newMsg["primaryLoad"] = 0;

// Rounded primary load
newMsg["primaryLoadRounded"] = Math.round(newMsg["primaryLoad"]);

// COP rounded at 1 decimal place
if (newMsg["primaryLoad"] > 0 && newMsg["compressorPower"] > 0)
    newMsg["cop"] = Math.round(newMsg["primaryLoad"] / newMsg["compressorPower"] * 100) / 100;
else
    newMsg["cop"] = null;

return newMsg;

