var newMsg = {payload: msg.payload};

for (const register of cx34map) {
  var val = msg.payload[register.index];

  if (register.measurement=="temperatureF")
    val = Math.round(val * 9 / 5 + 32);

  if (register.measurement == "deltaTemperatureF")
    val = Math.round(val * 9 / 5 * 10) / 10;

  if (register.measurement == "switch")
    val = val > 0;

  if (register.measurement == "flow")
    val = Math.round(val * 0.26417 * 10) / 10;

  if (register.measurement == "current")
    val = val;

  if (register.measurement == "mode")
    val = register.modes[val];

  newMsg[register.name] = val;
}

let cx34 = flow.get("cx34");
cx34.coolingSetpoint = newMsg.coolingSetpointC;
cx34.heatingSetpoint = newMsg.heatingSetpointC;
cx34.coolingSetpointF = newMsg.coolingSetpointF;
cx34.heatingSetpointF = newMsg.heatingSetpointF;
cx34.hysteresisF = newMsg.hysteresisF;
cx34.flowSwitchType = newMsg.flowSwitchType;
flow.set("cx34", cx34);

return newMsg;

// Normalize array to use register number instead of starting at index 0
const indexBase = Number(msg.modbusRequest.address);
let newArray = Array(indexBase);
msg.payload = newArray.concat(msg.payload);

const cx34map = [
  { index: 140, name: "run", measurement: "mode", modes: ["off", "on"] },
  { index: 141, name: "mode", measurement: "mode", modes: ["off", "cool", "heat", "dhw"] },
]

let newMsg = {payload: msg.payload};

for (const register of cx34map) {
  let val = msg.payload[register.index];

  if (register.measurement=="temperature")
    val = val * 9 / 5 + 32;

  if (register.measurement == "deltaTemperature")
    val = Math.round(val * 9 / 5 * 10) / 10;

  if (register.measurement == "switch")
    val = val > 0;

  if (register.measurement == "flow")
    val = Math.round(val * 0.26417);

  if (register.measurement == "current")
    val = val;

  if (register.measurement == "mode")
    val = register.modes[val];

  newMsg[register.name] = val;
}

let cx34 = flow.get("cx34");
cx34.run = newMsg.run;
cx34.mode = newMsg.mode;
flow.set("cx34", cx34);

return newMsg;

if (msg.payload != msg.previousValue) {
  let val = msg.payload;

  if (msg.measurement == "temperature")
    val = Math.round((val - 32) * 5 / 9);

  if (msg.measurement == "deltaTemperature")
    val = Math.round(val * 5 / 9);

  if (msg.measurement == "mode")
    val = msg.modes.indexOf(val);

  msg.payload = {
    value: val,
    fc: msg.fc, 
    unitid: msg.unitid, 
    address: msg.address, 
    quantity: msg.quantity
  }
  return msg;
}

// Initialize acc to 0 if it doesn't exist already
