return [htgMsg, clgMsg];

let iconTrue = "mdi:pump";
let iconFalse = "mdi:pump-off";

let pumpP = {}
if (msg.primaryPump) { pumpP = {"icon": iconTrue}}
else { pumpP = {"icon": iconFalse}};
const pumpMsg = { payload: pumpP};

return pumpMsg;

let cx34 = flow.get("cx34");
let compressorSpeed = cx34.compressorSpeed;

let lastTrigger = context.get("lastTrigger");

// Threshold on compressor speed
let threshold = 10;
// Delay after compressor OFF and before activating circulation
let delay = 5 * 60 * 1000; // Delay 5 minutes, in ms

// Set  buffer tank setpoint according to house-wide mode
const fcuMode = global.get("fcuMode", "persist");
const buffer_temp = msg.buffer_temperature;
// And current heat pump temperature settings
const coolingSetpoint = cx34.coolingSetpointF;
const heatingSetpoint = cx34.heatingSetpointF;
const hysteresis = cx34.hysteresisF;
const heatingStart = heatingSetpoint - hysteresis;
const coolingStart = coolingSetpoint + hysteresis;

// Determine if circulation is needed
let circulation_needed = false;
if (fcuMode == "Heating" && buffer_temp < heatingStart) {
    circulation_needed = true;
} else if (fcuMode == "Cooling" && buffer_temp > coolingStart) {
    circulation_needed = true;
}

// Default pump mode
let pump_mode = "stop when setpoint met";

// Choose mode
if (compressorSpeed >= threshold) {
    lastTrigger = Date.now();
    pump_mode = "stop when setpoint met";
} else if ((Date.now() - lastTrigger) > delay && circulation_needed) {
    pump_mode = "run continuously";
}

// Store new values
context.set("lastTrigger", lastTrigger);

// Return pump_mode value
return {primaryPumpMode: pump_mode};

